import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { todasTransacciones } from '../funciones/funciones'

const Trades = () => {

    const [trades, setTrades] = useState(null)
    const params = useParams()

    useEffect(() => {
        todasTransacciones(params.market_id, setTrades)
    }, [])

    const now = new Date()  
    const utcMiliSegundos = now.getTime() + (now.getTimezoneOffset() * 60 * 1000)  
    const utcSegundos = Math.round(utcMiliSegundos / 1000)  
    const timestamp = utcSegundos - 60 * 60 * 24
    return(
        <>
        <h1> Trades: {params.market_id}</h1>

        {trades != null ? (
            trades.map(todasTransacciones => (
                <div key={todasTransacciones.market_id}>
                    <h1> Hay transacciones </h1>
                    <h2> Son: {todasTransacciones.market_id} </h2>
                    <h2> Son: {trades.entries} </h2>
                    <h2> Son: {trades.last_timestamp} </h2>
                </div>
            ))
        ) : (
            <div>
                <h3>
                    No hay transacciones disponibles
                </h3>
            </div>
        )}

        <a
            className="App-link"
            href="/"
            rel="noopener noreferrer"
            >
            Volver al inicio
        </a>
        </>
    )
}
export default Trades
